routines.routine:
if (player.holder == TOP OF DEBRIS)
{
     printl("AS YOU SQUIRM THROUGH THE NARROW GAP, THE ROCKS GIVE WAY CAUSING YOU TO FALL MANY FEET TO THE TUNNEL FLOOR.");
}


fish = 6;

if (player.holder == TOP OF SHAFT)
{
	if (wooden crate.holder==player)
	{
		println("YOU CAN'T FIT THROUGH THE OPENING WHILE CARRYING THE CRATE.");
	}
	else
	{
		move();
	}
}
else
{
	move();
}

if (paperclip.holder == player)
{
     if (lockPicked== 0)
     {
          println("AFTER SEVERAL ATTEMPTS, YOU MANAGE TO UNLOCK AND OPEN THE DOOR.");
          door.open=true;
          door.openable=true;
          door.locked=false;
          door.lockable=false;
          add(score,20);
          lockPicked=1;
     }
     else
     {
          println("THE DOOR IS ALREADY OPEN.");
     }
}
else
{
     printl("YOU HAVE NOTHING TO DO THAT WITH.");
}

if (cigarette lighter.holder == player)
{
     if (pile of leaves.holder != player)
     {
          if (player.holder == base of shaft)
          {
               printl("FUELED BY THE DRAFT FROM ABOVE, THE LEAVES EVAPORATE IN CLOUD OF ACRID SMOKE WHICH RISES QUICKLY UP THE SHAFT.");
               add(score,20);
               on ladder.up=top of shaft;
               burnedLeaves=1;
               pile of leaves.holder=offscreen;
          }
          else if (player.holder == on ladder)
          {
               printl("YOU CAN'T DO THAT WHILE ON A LADDER");
          }
          else
          {
               printl("THE LEAVES SMOLDER A BIT, THEN GO OUT DUE TO THE HIGH HUMIDITY.");
          }
     }
     else
     {
          printl("THE LEAVES (WHICH YOU ARE CARRYING) IGNTIE. UNFORTUNATELY, THE FIRE RAPIDLY SPREADS TO YOU.");
          printl("NEXT TIME TRY DROPPING THE LEAVES.");
          call kill_player();
     }
}
else
{
     printl("YOU HAVE NO IGNITION SOURCE.");
}

printl("AS YOU RELUCTANTLY PICK UP THE PAPPERS, THEY DISINTEGRATE, LEAVING YOU WITH A BLACKENED PAPERCLIP.");
pile of gunk.holder=offscreen;
paperclip.holder=player;
add(score,5);

if (flashlight.lit==1)
{
	printl("IT'S ALREADY ON.");
}
else
{
	printl("CLICK.");
	flashlight.lit=1;
	look();
}

if (flashlight.lit==0)
{
	printl("IT'S ALREADY OFF.");
	flashlight.lit=0;
	look();
}
else
{
	printl("CLICK.");
	flashlight.lit=0;
}

if (paperclip.holder==offscreen)
{
     printl("THE REMAINING WATER IN THE TANK WASHES AWAY THE GUNK, LEAVING A PAPERCLIP AT THE BOTTOM OF THE BOWL.");
     paperclip.holder=filthy toilet;
     pile of gunk.holder=offscreen;
     add(score,5);
}
else
{
     println("NOTHING HAPPENS.");
}

if (wrench.holder == player)
{
     if (player.holder == top of shaft)
     {
          if (cupola.holder==top of shaft)
          {
               printl("WITH SOME DIFFICULTY, YOU MANAGE TO REMOVE THE BOLTS. AS YOU PUSH THE CUPOLA UP, IT FALLS OUT OF THE WAY.");
               cupola.holder=0;
               cupola.initial_description="THERE IS A HEAVY, METAL CUPOLA ON THE GROUND.";
               cupola.description="THE HEAVY METAL LID HAS STARTED RUSTING WITH AGE.";
               top of shaft.description="YOU ARE AT THE TOP OF A VENTILATION SHAFT. ABOVE, AN OPEN HOLE LEADS OUTSIDE.";
               cupola.scenery=0;
               top of shaft.up = top of hill;
               add(score,10);
          }
          else
          {
               println("YOU'VE ALREADY DONE THAT.");
          }
     }
     else
     {
          printl("YOU CAN'T DO THAT HERE.");
     }
}
else
{
     printl("YOU DON'T HAVE ANYTHING TO DO THAT WITH.");
}

if (player.holder==top of shaft)
{
	println("FOR SOME INEXPLICABLE REASON YOU DECIDE TO JUMP OFF THE LADDER.");
	println("UNFORTUNATELY, THE ROCK FLOOR AT THE BOTTOM OF THE SHAFT ISN'T QUITE AS FORGIVING AS YOU HAD ANTICIPATED.");
	call kill_player();
}
else if (player.holder==on ladder)
{
	println("YOU LAND WITH A THUD AT THE BOTTOM OF THE SHAFT.");
	player.holder = base of shaft;
}
else 
{
	println("WHEEEE!");
}

println("***YOU HAVE DIED***");
player.holder=2;
flashlight.holder=1;
look();

if (player.holder == top of shaft)
{
     println("THE CRATE FALLS DOWN THE SHAFT AND SMASHES AT THE BOTTOM.");
     wooden crate.holder=offscreen;
     splintered wood.holder=base of shaft;
     gold bars.holder=base of shaft;
}
else if (player.holder == on ladder)
{
     println("THE CRATE FALLS DOWN THE SHAFT, NEARLY SMASHING.");
     wooden crate.holder=base of shaft;
}

if (pair of gloves.beingworn == 1)
{
     println("THE COIN SNAPS NEATLY INTO THE RECESS.");
     println("THROUGH THE CAVERNS, YOU CAN HEAR THE DISTANT SOUND OF PUMPS RUNNING.");
     flooded tunnel.nw=base of shaft;
     flooded tunnel.w=255;
     flooded tunnel.sw=radio works;
     volt meter.description = "THE NEEDLE INDICATES CURRENT IS FLOWING.";
     flooded tunnel.description = "THIS IS A LOW POINT AT THE END OF AN E-W TUNNEL. THE MAIN TUNNEL LEADS EAST. SMALLER PASSAGES LEAD NORTHWEST AND SOUTHWEST.";
     add(score,20);
     coin.portable=0;
     coin.holder = small round recess;
     water.holder=offscreen;
}
else
{
     println("***ZAP***");
     println("A STRONG ELECTRICAL SHOCK KNOCKS YOU BACK.");
}

if (treeDown == 0)
{
     println("AFTER SEVERAL HARD SHOVES, THE TREE TOPPLES OVER, TAKING OUT A SECTION OF THE WIRE LARGE ENOUGH TO PASS THROUGH.");
     57.n =overgrown road;
     treeDown=1;
     rotting tree.initial_description = "A ROTTING TREE LIES ON THE GROUND.";
     57.description = "YOUR ARE STANDING IN THE MIDST OF DENSE FOREST.  WHAT'S LEFT OF A BARBED WIRE FENCE, NO LONGER BLOCKS THE WAY NORTH.";
     add(score,5);
}
else
{
     println("THE TREE IS ALREADY DOWN.");
}

