     println("THE TRUNK DOESN'T HAVE ANY BRANCHES SUITABLE FOR CLIMBING.");
}

if (gotGold==0)
{
     add(score,20);
     gotGold=1;
}

if ($dobj.portable == 1)
{
     if ($dobj.holder != PLAYER)
     {
          println("(TAKEN)");
          $dobj.holder = PLAYER;
     }
}

if (wrench.holder != PLAYER)
{
     println("THE CUPOLA IS BOLTED SHUT");
}
else
{
     println("(WITH THE WRENCH)");
     println("AFTER REMOVING THE BOLTS, YOU PUSH THE CUPOLA UP OUT OF THE WAY");
     top of shaft.up = top of hill;
     cupola.holder = offscreen;
     bolts.holder = offscreen;
}

if ($dobj != wooden crate)
{
     if (player.holder == top of shaft)
     {
          println("IT FALLS TO THE BOTTOM OF THE SHAFT.");
          $dobj.holder = base of shaft;
     }
     if (player.holder == on ladder)
     {
          println("IT FALLS TO THE BOTTOM OF THE SHAFT.");
          $dobj.holder = base of shaft;
     }
}

events.event:
if (player.holder==on ladder) 
{ 
	if (on ladder.up != top of shaft)
	{
		printl("HIGH ABOVE, YOU CAN HEAR BUZZING."); 
	}
}

if (player.holder == TOP OF HILL)
{
     if ($gameOver == 0)
     {
          if ($score == 100)
          {
               $gameOver =1 ;
               println("CONGRATULATIONS.  YOU'VE COMPLETED THIS ADVENTURE!");
               println("TYPE 'QUIT' TO QUIT.");
          }
          else
          {
               println("CONGRATULATIONS.  YOU'VE ESCAPED BUT DON'T HAVE A FULL SCORE.");
          }
     }
}

if ($turnsWithoutLight == 4)
{
     println("***CRASH***");
     println("IT APPEARS THE CAVERNS HAVE COLLAPSED ON YOU.");
     println("I SUPPOSE THAT'S WHAT HAPPENS WHEN YOU EXPLORE WITHOUT A LIGHT SOURCE.");
     println("LET'S TRY THIS ADVENTURE AGAIN.");
     player.holder = dirt road;
     flashlight.holder = dirt road;
     look();
}
